        page.run()

        this.$assert( logs, [ 'Hello Jin!' ] )

    }
}
```

–û—Ç–ª–∏—á–Ω–æ, —Ä–∞–±–æ—Ç–∞–µ—Ç. –¢–µ–ø–µ—Ä—å —É—Å–ª–æ–∂–Ω—è–µ–º –∑–∞–¥–∞—á—É - –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª—è–µ–º –∫–ª–∞—Å—Å –¥–ª—è –ø–æ–¥–¥–µ—Ä–µ–≤–∞ –æ–±—ä–µ–∫—Ç–æ–≤. –°–æ–∑–¥–∞–¥–∏–º –Ω–æ–≤—ã–π –∫–ª–∞—Å—Å –¥–ª—è –∫–∞—Ä—Ç–æ—á–∫–∏, –∫–æ—Ç–æ—Ä—ã–π –ø–æ–∑–≤–æ–ª—è–µ—Ç –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª—è—Ç—å –∏–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è, —Å–æ—Ö—Ä–∞–Ω—è—è –µ–≥–æ –≤ –ª–æ–∫–∞–ª—å–Ω–æ–µ —Ö—Ä–∞–Ω–∏–ª–∏—â–µ.

```typescript
namespace $ {
    export class $app_card extends $.$hello_card {
        
        get $() {
            const form = this
            return super.$.$ambient({
                get $user_name() { return form.user_name },
                set $user_name( next: string ) { form.user_name = next }
            })
        }

        get user_name() {
            return super.$.$storage_local.getItem( 'user_name' ) ?? super.$.$user_name
        }
        set user_name( next: string ) {
            super.$.$storage_local.setItem( 'user_name', next )
        }

    }
}
```

–°–∞–º–æ –ª–æ–∫–∞–ª—å–Ω–æ–µ —Ö—Ä–∞–Ω–∏–ª–∏—â–µ - —ç—Ç–æ –ø—Ä–æ—Å—Ç–æ –∞–ª–∏–∞—Å –¥–ª—è –Ω–∞—Ç–∏–≤–Ω–æ–≥–æ –æ–±—ä–µ–∫—Ç–∞:

```typescript
